CREATE TRIGGER test_trigger
BEFORE INSERT ON t_person FOR EACH ROW
IF NEW.AGE>50 THEN
SIGNAL SQLSTATE '45000'
SET MESSAGE_TEXT = 'Testtesttest';
END IF;

CREATE TRIGGER test_trigger5
BEFORE INSERT ON t_person FOR EACH ROW
IF NEW.AGE>50 THEN
insert into person.t_person(id,age,firstname,lastname) values (new.99,new.77, new.'sdfs', new.'ghhhh');
END IF;
